font_names = list(FONTS.keys())


def get_font_styles(font_name: str) -> list:
    return list(FONTS[font_name].keys())


def get_font(font_name: str, font_style: str) -> list:
    return FONTS[font_name][font_style]


def get_char_index(char: str) -> int:
    code = ord(char)
    if char in "ABCDEFGHIJKLMNOPQRSTUVWXYZ":
        index = code - ord("A")
    elif char in "abcdefghijklmnopqrstuvwxyz":
        index = code - ord("A") - (ord("a") - ord("Z")) + 1
    elif char in "0123456789":
        index = code - ord("0") + ord("z") - ord("A") - (ord("a") - ord("Z")) + 2
    else:
        index = 10000000
    return index


def convert(string: str, font_name: str = None, font_style: str = None) -> str:
    font_name = font_name or font_names[0]
    font_style = font_style or get_font_styles(font_name)[0]
    font = get_font(font_name, font_style)
    chars = []
    for char in string:
        try:
            chars.append(font[get_char_index(char)])
        except IndexError:
            chars.append(char)
    return "".join(chars)
